export { mainShare, whatsAppShare };

const mainShare = (event) => {
  event.preventDefault();
  //
  const fbShare = () => {
    var baseURL = "https://www.facebook.com/sharer/sharer.php";
    var u =
      "https://cloud.greenhk.greenpeace.org/petition-oceans-elm?utm_campaign=2020-elm&utm_source=facebook&utm_medium=social&utm_content=main_share#/";
    var t = (window.innerHeight - 436) / 2;
    var l = (window.innerWidth - 626) / 2;
    window.open(
      baseURL + "?u=" + encodeURIComponent(u),
      "_blank",
      "width=626,height=436,top=" + t + ",left=" + l
    );
  };
  // WEB SHARE API
  if (navigator.share) {
    navigator
      .share({
        title: "",
        text:
          "綠色和平最新調查發現涉及非法毀林的巴西肉商，包括JBS、 Marfrig、Minerva均以香港為主要牛肉出口市場之一，而國際連鎖快餐店McDonald’s、KFC、Burger King則在不同國家涉及使用以破壞森林生產大豆為飼料養殖的肉品；消費者或在不知情下成為毀林幫兇。👉 ",
        url: "https://act.gp/2XlKLpC",
      })
      .then()
      .catch();
  } else {
    fbShare();
  }
};

const whatsAppShare = (event) => {
  event.preventDefault();
  var w =
    "https://api.whatsapp.com/send?text=綠色和平最新調查發現涉及非法毀林的巴西肉商，包括JBS、 Marfrig、Minerva均以香港為主要牛肉出口市場之一，而國際連鎖快餐店McDonald’s、KFC、Burger King則在不同國家涉及使用以破壞森林生產大豆為飼料養殖的肉品；消費者或在不知情下成為毀林幫兇。👉 https://act.gp/2KCo7Cx";
  window.open(w);
};
